        public async Task<DepuracionesOutputModel> GetListaDepuracionOutputModelAsync(int entidadId, string busqueda, DepuracionContable.CausaDepuracion? causaDepuracion,
            DepuracionContable.EstadoPago? estadoPago, int pagina, int tamanoPagina, int userId, DepuracionContable.EstadoDepuracion? estadoDepuracion)
        {
            TblUsuarios usuario = await _usuariosService.ObtenerUsuarioDetalle(userId);
            var spec = new DepuracionesPorFichaTec_DescFichayFichaTecAprobSpec(entidadId, busqueda, causaDepuracion, estadoPago, (pagina - 1) * tamanoPagina, tamanoPagina, userId, estadoDepuracion);
            var depuraciones = await _depuracionContableRepo.ListAsync(spec);
            var specTotal = new DepuracionesPorFichaTec_DescFichayFichaTecAprobSpec(entidadId, busqueda, null, null, null, null, userId, estadoDepuracion);
            var total = await _depuracionContableRepo.CountAsync(specTotal);
            var pucs = await _pucRepo.ListAllAsync();
            if (depuraciones is null)
                return null;
            
            var depuracionesmodel = _mapper.Map<IReadOnlyList<DepuracionOutputModel>>(depuraciones);
            foreach (DepuracionOutputModel item in depuracionesmodel)
            {
                var depuracion = depuraciones.Where(c => c.Id == item.Id).First();
                long? credito = depuracion.Movimientos.Where(c => c.Credito > 0).FirstOrDefault()?.CodigoContable;
                if (credito != null) {
                    var puc = pucs.Where(c => c.Codigo == credito.ToString() && c.ContabilidadId == depuracion.ContabilidadId).FirstOrDefault();
                    if (puc != null)
                    {
                        item.CodyNombreCuenta = puc.Codigo + "-" + puc.Descripcion;
                    }
                    else {
                        item.CodyNombreCuenta = credito.ToString();
                    }
                }

                switch (item.Estado)
                {
                    case "Borrador":
                        if(estadoDepuracion == null) 
                            item.Accion = "Completar";
                        item.Editable = true;
                        break;
                    case "PorPreaprobar":
                        depuracion = depuraciones.Where(c => c.Id == item.Id).First();
                        if((depuracion.GerenteId == userId && depuracion.EstadoAprobacionGerente == null) ||
                            (depuracion.InterventorId == userId && depuracion.EstadoAprobacionInterventor == null) ||
                            (depuracion.CoordinadorId == userId && depuracion.EstadoAprobacionCoordinador == null) && estadoDepuracion == null)
                            item.Accion = "Gestionar";
                        break;
                    case "Preaprobada":
                        if (estadoDepuracion == null)
                            item.Accion = "Gestionar";
                        break;
                    case "Aprobada Contador":
                        depuracion = depuraciones.Where(c => c.Id == item.Id).First();
                        if((userId == depuracion.Operativo_1_Id || userId == depuracion.Operativo_2_Id) && estadoDepuracion == null)
                            item.Accion = "Gestionar";
                        break;
                    case "Aprobada Operativo":
                        depuracion = depuraciones.Where(c => c.Id == item.Id).First();
                        if ((userId == depuracion.Contador_1_Id || userId == depuracion.Contador_2_Id) && estadoDepuracion == null)
                            item.Accion = "Gestionar";
                        break;
                    default:
                        break;
                }
            }

            depuracionesmodel = depuracionesmodel.OrderBy(c => c.NoFichaTecnica).ToList();
            DepuracionesOutputModel depuracionesOutputModel = new DepuracionesOutputModel
            {
                depuracionOutputModel = depuracionesmodel,
                paginacion = new PaginacionModel(total, pagina, tamanoPagina),
                aprobador = usuario.Entidades.Where(c=>c.EntidadId == entidadId).First().usuarioEntidadPerfiles.First().PerfilId == 11
            };
            return (depuracionesOutputModel);
        }

        public async Task<DetalleDepuracionOutputModel> ObtenerDepuracionPorIdAsync(Guid Id, int entidadId, int userId) {
            var spec = new DepuracionContableConIdSpec(Id, entidadId);
            var depuracion = await _depuracionContableRepo.GetOneAsync(spec);
            depuracion.EliminarMovimientosInactivos();
            var outputModel = _mapper.Map<DetalleDepuracionOutputModel>(depuracion);
            outputModel.TotalDebito = depuracion.TotalDebitos();
            outputModel.TotalCredito = depuracion.TotalCreditos();
            return outputModel;
        }

        public DetalleDepuracionOutputModel GetOutputModel(DepuracionContable depuracionContable) {
            return null;
        }

