            string firmaOperativo1 = "";
            string firmaOperativo2 = "";
            if (depuracionContable.Estado == DepuracionContable.EstadoDepuracion.Aprobada_Contador || depuracionContable.Estado == DepuracionContable.EstadoDepuracion.Aprobada)
