            string nombreInterventor = usuarios.Where(c => c.IIdusuario == depuracionContable.InterventorId).FirstOrDefault()?.ObtenerNombre();
            string nombreContador1 = usuarios.Where(c => c.IIdusuario == depuracionContable.Contador_1_Id).FirstOrDefault()?.ObtenerNombre();
