            int renglonesAccionesRealizadas = depuracionContable.AccionesRealizadas == null ? 0 : (int)Math.Ceiling(depuracionContable.AccionesRealizadas.Length / 150.00);
            int renglonesDisposicionesLegales = depuracionContable.DisposicionesLegales == null ? 0 : (int)Math.Ceiling(depuracionContable.DisposicionesLegales.Length / 150.00);
            int renglonesRecomendaciones = depuracionContable.Recomendaciones == null ? 0 : (int)Math.Ceiling(depuracionContable.Recomendaciones.Length / 150.00);
            int renglonesAnexos = depuracionContable.Anexos == null ? 0 : (int)Math.Ceiling(depuracionContable.Anexos.Length / 150.00);

            renglonesSitucacionEncontrada += depuracionContable.SituacionEncontrada == null ? 0 : depuracionContable.SituacionEncontrada.Split("<br /><br />").Count() - 1;
            renglonesAccionesRealizadas += depuracionContable.AccionesRealizadas == null ? 0 : depuracionContable.AccionesRealizadas.Split("<br /><br />").Count() - 1;
            renglonesDisposicionesLegales += depuracionContable.DisposicionesLegales == null ? 0 : depuracionContable.DisposicionesLegales.Split("<br /><br />").Count() - 1;
            renglonesRecomendaciones += depuracionContable.Recomendaciones == null ? 0 : depuracionContable.Recomendaciones.Split("<br /><br />").Count() - 1;
            renglonesAnexos += depuracionContable.Anexos == null ? 0 : depuracionContable.Anexos.Split("<br /><br />").Count() - 1;

