            html += "<tr><td colspan=\"12\" style=\"border-bottom:2px solid #D9DEE4\"><span class=\"tituloAzul\" style=\"font-weight:bold\">ANEXOS</span></td></tr>";

            //LINK RETHUS
            string registro_rethus = "";
            string linkrethus = "";
            string origin = "";
            try
            {
                registro_rethus = depuracionContable.SituacionEncontrada.Split("identificación número")[1].Substring(0, 20).Trim();
                registro_rethus = registro_rethus.Split(' ')[0].Trim();
                registro_rethus = registro_rethus.Replace(",", "");
                origin = _configuration.GetValue<string>("Origin");
                linkrethus = origin + "/#/pages/dashboard/" + access_token + "/" + entidadId.ToString() + "/null/1/9/" + registro_rethus;
