        private async Task<string> ObtenerFirmaBase64(IReadOnlyList<TblUsuarios> usuarios, int? usuarioId) {
            try
            {
                if (usuarioId != null)
                {
                    var usuario = usuarios.Where(c => c.IIdusuario == usuarioId).FirstOrDefault();
                    if (usuario != null)
                    {
                        if (usuario.Firma != null)
                        {
                            MemoryStream streamFirma = await _blobService.GetBlobFileByGuidAsync(usuario.Firma, "firmas");
                            var bytes = streamFirma.ToArray();
                            return Convert.ToBase64String(bytes);
                        }
                    }
                }
                return "";
            }
            catch (Exception)
            {
                return "";
            }
        }
        private async Task<string> ObtenerLogoBase64(string logo)
        {
            if (logo != null)
            {
                {
                    try
                    {
                        MemoryStream streamFirma = await _blobService.GetBlobFileByGuidAsync(logo, "logos");
                        var bytes = streamFirma.ToArray();
                        return Convert.ToBase64String(bytes);
                    }
                    catch (Exception)
                    {
                        return "";
                    }
                }
            }
            return "";
        }
    }
